import React, {useState, useEffect} from "react";

// npm i --save react-bootstrap
// npm install react-bootstrap-validation --save

import Dropdown from "react-bootstrap/Dropdown";

function App() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    const [value,setValue]=useState('');

    value = "Select Category";

    const handleSelect=(e)=>{
        console.log(e);
        setValue(e)
      }

    useEffect(() => {
        fetch("http://localhost:9000/Websites")
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setItems(result);
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
          )
      }, [])

      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
            let categories = [];
            if (items.categoryArray !== undefined)
            {
                categories = Object.entries(items.categoryArray).map(([key, value]) => 
                {
                    return <Dropdown.Item href={key} id={key}>{value}</Dropdown.Item>
                });
                console.log("categories " + categories[0]);
            }
        
        return (
            <div className="mybody">
                <section id="websites">
                    <containerfluid>
                        <div>
                            <table className="table table-hover table-success">
                                <thead className="thead-dark">
                                <tr>
                                    <th scope="col">URL</th>
                                    <th scope="col">Category</th>
                                    <th scope="col">Comment</th>
                                </tr>
                                </thead>
                                <tbody id="header">
                                </tbody>
                            </table>
                            <div>
                                <table class="table">
                                <tr>
                                    <th class="pageinfo" id="currentPageDetails"></th>
                                    <th>
                                    <nav aria-label="Page navigation example">
                                        <ul className="pagination justify-content-end">
                                        <li className="page-item"><a class="page-link" href="/paging?command=begin&value=1" id = "begin">&#8678;</a></li>
                                        <li className="page-item"><a class="page-link" href="/paging?command=previous&value=-1" id = "previous"      
                                            >Previous</a></li>
                                        <li className="page-item"><a class="page-link" href="/paging?command=link&value=1" id = "link1"  >1</a></li>
                                        <li className="page-item"><a class="page-link" href="/paging?command=link&value=2" id = "link2" >2</a></li>
                                        <li className="page-item"><a class="page-link" href="/paging?command=link&value=3" id = "link3" >3</a></li>
                                        <li className="page-item"><a class="page-link" href="/paging?command=next&value=0" id = "next" 
                                            >Next</a></li>
                                        <li className="page-item"><a class="page-link" href="/paging?command=end&value=1" id = "end">&#8680;</a></li>
                                        </ul>
                                    </nav>
                                    </th>
                                </tr>
                                </table>
                            </div>
                        </div>
                    </containerfluid>
                </section>

                <section id="website-search">
                <containerfluid2>
                    <div>
                    <Dropdown>
                        <Dropdown.Toggle variant="secondary" id="dropdown-basic" onSelect={handleSelect}>
                            {value}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            {categories}
                        </Dropdown.Menu>
                    </Dropdown>
                        <table className="table table-hover table-success">
                            <thead className="thead-dark">
                            <tr>
                                <th scope="col">URL</th>
                                <th scope="col">Category</th>
                                <th scope="col">Comment</th>
                            </tr>
                            </thead>
                            <tbody id="header2">
                            </tbody>
                        </table>
                    </div>
                </containerfluid2>
                </section>
            </div>
        );
      }
}

export default App;
