import React, {useState, useEffect} from "react";

// npm i --save react-bootstrap
// npm install react-bootstrap-validation --save

import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";

// function App() {
//     const [value,setValue]=useState('');
//     const handleSelect=(e)=>{
//       console.log(e);
//       setValue(e)
//     }
//     return (
//       <div className="App container">
        
//         <DropdownButton
//         alignRight
//         title="Dropdown right"
//         id="dropdown-menu-align-right"
//         onSelect={handleSelect}>
//                 <Dropdown.Item eventKey="option-1">option-1</Dropdown.Item>
//                 <Dropdown.Item eventKey="option-2">option-2</Dropdown.Item>
//                 <Dropdown.Item eventKey="option-3">option 3</Dropdown.Item>
//                 <Dropdown.Divider />
//                 <Dropdown.Item eventKey="some link">some link</Dropdown.Item>
//         </DropdownButton>
//         <h4>You selected {value}</h4>
//       </div>
//     );
//   }

// let results = [];

function App() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    const [value, setValue]=useState();
    const [items2, setItems2] = useState([]);
    const [results, setResults]=useState([]);

    const handleSelect=(e)=>{
        console.log(e);
        setValue(e);
      }

    function handleBegin(command, value){
        let url = "http://localhost:9000/Websites/paging?" + command + "&value=" + value;
        console.log(url);
        fetch(url)
            .then(res => res.json())
            .then(
                (result) => 
                {
                    setIsLoaded(true);
                    setItems2(result);
                },
                    // Note: it's important to handle errors here
                    // instead of a catch() block so that we don't swallow
                    // exceptions from actual bugs in components.
                (error) => 
                {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    
            if (error) 
            {
                return <div>Error: {error.message}</div>;
            } 
            else if (!isLoaded) 
            {
                return <div>Loading...</div>;
            } 
            else 
            {
                if (items2.returnValue !== undefined)
                {
                    let results = Object.entries(items2.returnValue).map(([key, value]) => 
                    {
                        return (<tr>
                            <th scope="row">
                                <a href={value.url} target="_blank">{value.url}</a>
                            </th>
                            <td>
                                {value.Category}
                            </td>
                            <td>
                                {value.Comment}
                            </td>
                        </tr>)
                    });
                    setResults(calcResults);
                }
            }
    }

    useEffect(() => {
        fetch("http://localhost:9000/Websites")
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setItems(result);
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
          )
      }, [])

      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
            let categories = [];
            if (items.categoryArray !== undefined)
            {
                categories = Object.entries(items.categoryArray).map(([key, value]) => 
                {
                    return <Dropdown.Item eventKey={value}>{value}</Dropdown.Item>
                });
            }

            if (items.results !== undefined)
            {
                results = Object.entries(items.results.returnValue).map(([key, value]) => 
                {
                    return (<tr>
                        <th scope="row">
                            <a href={value.url} target="_blank">{value.url}</a>
                        </th>
                        <td>
                            {value.Category}
                        </td>
                        <td>
                            {value.Comment}
                        </td>
                    </tr>)
                });
            }
        
        return (
            <div className="mybody">
                <section id="websites">
                        <div className="containerfluid">
                            <table className="table table-hover table-success">
                                <thead className="thead-dark">
                                <tr>
                                    <th scope="col">URL</th>
                                    <th scope="col">Category</th>
                                    <th scope="col">Comment</th>
                                </tr>
                                </thead>
                                <tbody id="header">
                                {results}
                                </tbody>
                            </table>
                            <div>
                                <table className="table">
                                <tr>
                                    <th className="pageinfo" id="currentPageDetails"></th>
                                    <th>
                                    <nav aria-label="Page navigation example">
                                        <ul className="pagination justify-content-end">
                                        <li className="page-item"><a class="page-link" href="#" onClick={() => handleBegin("begin",1)} id = "begin">&#8678;</a></li>
                                        <li className="page-item"><a class="page-link" href="#" onClick={() => handleBegin("previous",-1)} id = "previous">Previous</a></li>
                                        <li className="page-item"><a class="page-link" href="#" onClick={() => handleBegin("link",1)} id = "link1"  >1</a></li>
                                        <li className="page-item"><a class="page-link" href="#" onClick={() => handleBegin("link",2)} id = "link2" >2</a></li>
                                        <li className="page-item"><a class="page-link" href="#" onClick={() => handleBegin("link",3)} id = "link3" >3</a></li>
                                        <li className="page-item"><a class="page-link" href="#" onClick={() => handleBegin("next",0)} id = "next">Next</a></li>
                                        <li className="page-item"><a class="page-link" href="#" onClick={() => handleBegin("end",1)} id = "end">&#8680;</a></li>
                                        </ul>
                                    </nav>
                                    </th>
                                </tr>
                                </table>
                            </div>
                        </div>
                </section>

                <section id="website-search">
                    <div className="containerfluid2">
                    <DropdownButton alignRight title={value} id="dropdown-menu-align-right" onSelect={handleSelect}>
                    {categories}
                    </DropdownButton>
                    <h4>You selected {value}</h4>
                        <table className="table table-hover table-success">
                            <thead className="thead-dark">
                            <tr>
                                <th scope="col">URL</th>
                                <th scope="col">Category</th>
                                <th scope="col">Comment</th>
                            </tr>
                            </thead>
                            <tbody id="header2">
                            </tbody>
                        </table>
                    </div>
                </section>
            </div>
        );
      }
    }
export default App;
